def exo_1
    
    puts $twitter_accounts.length
    
    short_size = -1
    short_account =""
    
    $twitter_accounts.each {
        
        |account|
        if short_size < 0
            then short_size = account.size
            
            elsif
            
            account.size < short_size
            then
            short_size = account.size
            short_account = account
            
        end
        
    }
    puts "Le compte le plus court a : #{short_size} caractères, son nom est #{short_account}"
    
end

