def exo_2
    
    handle_number =0
    
    $twitter_accounts.each {
        
        |account|
        
        if account.size == 6
            then handle_number +=1
        end
        
    }
    
    puts "Il y a #{handle_number} comptes qui contiennent 5 caractères."
    
end

#Combien commencent par une majuscule (première lettre juste après le @) ?

def exo_3
    
    capital_word = 0
    $twitter_accounts.each {
        
        |account|
        if account.byteslice(1).ord < 91 and account.byteslice(1).ord > 64
            then capital_word +=1
        end
        
    }
    puts "Il y a #{capital_word} qui commencent par une majuscule"
    
end

