#Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)

def exo_5
    
    
    
    def repartition(number_letter)
        number_name = 0
        $twitter_accounts.each {
            
            
            |account|
            
            if account.size == number_letter
                then number_name +=1
            end
        }
        
        puts "Il y a #{number_name} handle qui contiennent #{number_letter} lettres"
        
        return number_name
        
    end
    
    counter = 0
    i = 0
    while counter < $twitter_accounts.length
        
        resultat = repartition(i)
        counter += resultat
        i+=1
    end
    puts counter
    
end

